import React, { useState } from "react";
import Button from '@mui/material/Button';
import axios from "axios";
import { useHistory } from "react-router-dom";
import { FormGroup, FormControl, InputLabel, Input, styled } from '@mui/material';

// Styled form control for better styling
const StyledFormControl = styled(FormControl)`
  margin-bottom: 20px;
`;

const AddTeams = () => {
    const history = useHistory();

    const [user, setUser] = useState({
        name: "",
        id: "",
        TeamNumber: "",
        subj: "",
        pname: "",
        startdate: "",
        status: ""
    });

    const handleChange = e => {
        const { name, value } = e.target;
        setUser(prevUser => ({
            ...prevUser,
            [name]: value
        }));
    };

    const register = () => {
        const { id } = user;
        if (id) {
            axios.post("http://localhost:8000/add", user)
                .then(res => {
                    alert(res.data.message);
                    history.push("/all");
                })
                .catch(error => {
                    console.error("Error adding project:", error);
                    alert("Error adding project. Please try again.");
                });
        } else {
            alert("Invalid input");
        }
    };

    return (
        <div>
            <br />
            <div className="register">
                <h1>Register for Projects</h1>
                <div className="Control">
                    <StyledFormControl>
                        <InputLabel>Name</InputLabel>
                        <Input name="name" value={user.name} onChange={handleChange} />
                    </StyledFormControl>
                    &nbsp;&nbsp;
                    <StyledFormControl>
                        <InputLabel>Id</InputLabel>
                        <Input name="id" value={user.id} onChange={handleChange} />
                    </StyledFormControl>
                    <StyledFormControl>
                        <InputLabel>Team Number</InputLabel>
                        <Input name="TeamNumber" value={user.TeamNumber} onChange={handleChange} />
                    </StyledFormControl>
                    &nbsp;&nbsp;
                    <StyledFormControl>
                        <InputLabel>MSWD/PFSD</InputLabel>
                        <Input name="subj" value={user.subj} onChange={handleChange} />
                    </StyledFormControl>
                    <StyledFormControl>
                        <InputLabel>Project Name</InputLabel>
                        <Input name="pname" value={user.pname} onChange={handleChange} />
                    </StyledFormControl>
                    &nbsp;&nbsp;
                    <StyledFormControl>
                        <InputLabel>StartDate</InputLabel>
                        <Input name="startdate" value={user.startdate} onChange={handleChange} />
                    </StyledFormControl>
                    <StyledFormControl>
                        <InputLabel>Status</InputLabel>
                        <Input name="status" value={user.status} onChange={handleChange} />
                    </StyledFormControl>
                    <Button variant="contained" onClick={register}>Add Project</Button>
                </div>
            </div>
        </div>
    );
};

export default AddTeams;
