import React, { useState, useEffect } from 'react';

function WelcomePage() {
  const [words, setWords] = useState([]);
  const sentence = "Welcome to Project Management System";

  useEffect(() => {
    // Split the sentence into words
    const wordArray = sentence.split(" ");
    // Use setTimeout to set state for each word with a delay
    const timer = setTimeout(() => {
      setWords(wordArray);
    }, 500); // Adjust delay time (in milliseconds) as needed

    // Clear timeout on component unmount
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <h1 style={{ color: 'white' }}>Welcome to Project Management System</h1>
      <div>
        {words.map((word, index) => (
          <span key={index}>{word} </span>
        ))}
      </div>
    </div>
  );
}

export default WelcomePage;
