import React, { useState } from "react";
import Button from "@mui/material/Button";
import { useHistory } from "react-router-dom";
import {
  FormControlLabel,
  FormControl,
  InputLabel,
  Input,
  Box,
  RadioGroup,
  Radio,
  FormLabel,
} from "@mui/material";
import { addProject } from "../backend/api"; // import your API functions

// Styled form control for better styling
const StyledFormControl = ({ children }) => (
  <FormControl fullWidth sx={{ marginBottom: "20px" }}>
    {children}
  </FormControl>
);

const AddTeams = () => {
  const history = useHistory();

  const [user, setUser] = useState({
    name: "",
    id: "",
    TeamNumber: "",
    subj: "",
    pname: "",
    startdate: "",
    status: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const register = async () => {
    const { id } = user;
    if (id) {
      try {
        await addProject(user, history); // Pass history object
      } catch (error) {
        console.error("Error adding project:", error);
        alert("Error adding project. Please try again.");
      }
    } else {
      alert("Invalid input");
    }
  };


  return (
    <div style={{ marginTop: "20px" }}>
      <div className="register">
        <h1>Register for Projects</h1>
        <Box maxWidth="400px">
          <StyledFormControl>
            <InputLabel>Name</InputLabel>
            <Input name="name" value={user.name} onChange={handleChange} />
          </StyledFormControl>
          {/* Other form fields... */}
        </Box>
        <Button variant="contained" onClick={register}>
          Add Project
        </Button>
      </div>
    </div>
  );
};

export default AddTeams;
