import React, { useState, useEffect } from 'react';

function WelcomePage() {
  const [sentence, setSentence] = useState("Project,Management,System");

  useEffect(() => {
    // Set the entire sentence directly
    setSentence("Project Management System");
  }, []);

  return (
    <div>
      <h1 style={{ color: 'white', fontSize: '150%' }}>
        {sentence}&nbsp;
      </h1>
    </div>
  );
}

export default WelcomePage;
