import React, { useState, useEffect } from 'react';

function WelcomePage() {
  const [sentence, setSentence] = useState("Project Management System");

  useEffect(() => {
    const wordsArray = sentence.split(' ');
    let currentIndex = 0;

    const intervalId = setInterval(() => {
      if (currentIndex < wordsArray.length) {
        setSentence(prevSentence => {
          const nextWord = wordsArray[currentIndex];
          currentIndex++;
          return prevSentence.endsWith(nextWord) ? prevSentence : prevSentence + " " + nextWord;
        });
      } else {
        clearInterval(intervalId);
      }
    }, 1000); // Adjust the delay as needed

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div>
      <h1 style={{ color: 'white', fontSize: '150%' }}>
        {sentence}
      </h1>
    </div>
  );
}

export default WelcomePage;
